function listpenyusun(){
        
    console.log("sebelum="+namaval);
    $(document).ready(function () {
    var lnamaval=namaval;
    var lnikval=nikval;
    var lusernames = getCookie('usrnm');
    console.log(lusernames);

        $.ajax({
            type: "POST",
            url: "../RND_BackEnd/backend_project_bonkas/create_namapenerimaluar",

            headers: {
                user: vusrnm,
                token: vtoken,
            },
            data:{
                snama  : lnamaval,
                snik : lnikval,
                user : userNamelrnegeri
            },
                success: function (response){
            var obj = JSON.parse(response);
            console.log(obj);
            if(isEmpty(obj)) {
            console.log('Gagal save, data kosong');
            
            
            } else if(obj) {
            if (obj.status == false){
                Swal.fire('Error',obj.message,'error')
            }  else if (obj.code == 'error11'){
                Swal.fire('Error','data kosong','error')
            }  else {
                Swal.fire({
                text: 'Item berhasil ditambahkan',
                timer: 1000,
                position: 'top-right',
                toast: true,
                showConfirmButton: false
                })
                loadlistpenerima();
                namaval="";
                nikval="";
                console.log("sesudah="+namaval);
                $('#nama_penerima').val('');
            }
            
            } else {
            Swal.fire('Error','','error')
            }
        },
        error: function(){
            if(fungsilogout){

                window.location.href="./_logout.php";
            }else{
                console.log("error session")
            }
        }
        });
        
    });
    
}


