//FUNGSI AKTIVASI CALENDAR
function datepick(){
    $('#tanggalberangkat').calendar({
            type: 'date',
            monthFirst: false,
            // minDate : d,
            onChange: function(date){
                maxdateset= date;
                tanggalcek();
                console.log(maxdateset+"<--ini max date");
                // cekdate(date);
                
                
            },
        formatter: {
        date: function (date, settings) {
            if (!date) return "";
            var day = date.getDate() + "";
            if (day.length < 2) {
            day = "0" + day;
            }
            var month = date.getMonth() + 1 + "";
            if (month.length < 2) {
            month = "0" + month;
            }
            var year = date.getFullYear();
            return day + "-" + month + "-" + year;
            // return year + "-" + month + "-" + day;
        }
    }
        
    });

    
}

function datepickuang(){

    $('#tanggaluang').calendar({
        type: 'date',
        monthFirst: false,
        // minDate : d,
        maxDate : maxdateset,
    formatter: {
    date: function (date, settings) {
        if (!date) return "";
        var day = date.getDate() + "";
        if (day.length < 2) {
        day = "0" + day;
        }
        var month = date.getMonth() + 1 + "";
        if (month.length < 2) {
        month = "0" + month;
        }
        var year = date.getFullYear();
        return day + "-" + month + "-" + year;
        // return year + "-" + month + "-" + day;
    }
}
    
});
}

//--------------- CEK APAKAH TANGGAL BERANGKAT SUDAH DIISI ATAU BELUM
// ---------------------SUDAH DIISI --> BOLEH ISI TANGGAL UANG
function tanggalcek(){

    $('#tanggaluang').calendar('set date', undefined);

    if (maxdateset=="" || maxdateset==undefined){

        $("#tgl_uang").prop("disabled",true);
        document.getElementById("tgl_uang").placeholder = "input tgl berangkat terlebih dahulu";
    }else{
        $("#tgl_uang").prop("disabled",false);
        document.getElementById("tgl_uang").placeholder = "date";
    }
    datepickuang();
}

