 function loadlistpenerima(){

    var lusername    =  getCookie('usrnm');
    $('#tb_listnama').find('tbody').empty();
    
    $.ajax({
        type: "POST",
        url: "../RND_BackEnd/backend_project_bonkas/table_namapembelian",
        headers: {
            user: vusrnm,
            token: vtoken,
        },
        data: { user :userName1},
        cache:false,

        success: function (response){
            var obj = JSON.parse(response);
            console.log(obj);
            if(obj.status==false) {
                console.log('Gagal load tabel, data kosong');
                datapertama();
                
            }else {
                console.log("dataada");
                datakedua();
                jmlData = obj.length;
                console.log(jmlData);
                
                for(b = 0; b < jmlData; b++){
                    if(b===0){

                    $('#tb_listnama').find('tbody').append(
                        "<tr id='"+b+"'><td>"+obj[b]["nama_pembelian"]+
                        "</td><td class='center' width='400px'>"+obj[b]["nik_pembelian"]+
                        "</td><td class='center'></td></tr>");
                        
                    }
                
                else{
                $('#tb_listnama').find('tbody').append(
                "<tr id='"+b+"'><td>"+obj[b]["nama_pembelian"]+
                "</td><td class='center' width='400px'>"+obj[b]["nik_pembelian"]+
                "</td><td class='center'><i class='trash deletepenerima iconcoloredit icon' nmpenerima='"+obj[b]["nama_pembelian"]+"' delid='"+obj[b]["id_namapembelian"]+"' delnik='"+obj[b]["nik_pembelian"]+"'></i></td></tr>");
                }

                $('.deletepenerima').on('click',function(e){
                    var dnama= $(this).attr('nmpenerima');
                    var idnamapenerima1 = $(this).attr('delid');
                    var dnik= $(this).attr('delnik');
                Swal.fire({
                    title: '<h2>Hapus data ini?</h2>',
                    text: "DATA: "+dnik+" - "+dnama,
                    icon: 'warning',
                    showCancelButton: true,
                    confirmButtonText: 'Ya'
                    }).then((result) => {
                    if (result.value) {
                        //REST CALON DNPENYUSUN
                        $.ajax({
                        type: "POST",
                        url: "../RND_BackEnd/backend_project_bonkas/delete_namapembelian",
                        headers: {
                            user: vusrnm,
                            token: vtoken,
                        },
                        data: {
                            idnamapembelian : idnamapenerima1,
                            
                        },
                        cache: false,
                        success: function (response){
                            var obj = JSON.parse(response);
                            if (obj.code == 'error9'){
                            Swal.fire('Error','Ada kendal di koneksi/database, data tidak tersimpan !','error')
                            } else if (obj.code == 'error10'){
                            Swal.fire('Error','Item yang dimaksud tidak ada !','error')
                            }  else {
                            loadlistpenerima();
                            }
                        },
                        error: function(){
                            if(fungsilogout){
            
                                window.location.href="./_logout.php";
                            }else{
                                console.log("error session")
                            }
                        },
                        });
                    }
                    })
                
                
                });
            }

        }
    }
    


});


}

