$("#MKGList").on("click", ".SendReq", function () {
  var IdNum = $(this).attr("IdNum");
  if (IdNum){
    Swal.fire({
      title: "<h3>Kirim permintaan Barcode EAN?</h3>",
      confirmButtonText: "Ya",
      showCancelButton: true,
    }).then((result) => {
      if (result.value) {
        $.ajax({
          type: "POST",
          url: `${restlocean}SendRequestEAN`,
          headers: {
            user: vusrnm,
            token: vtoken,
          },
          data: {
            IdNum  : IdNum,
          },
          cache: false,
          success: function (response) {
            var obj = JSON.parse(response);
            if (obj.status == false) {
              Swal.fire(
                "Error",
                "Ada kendal di koneksi/database, data tidak tersimpan !",
                "error"
              );
            } else {
              Swal.fire({
                text: obj.message,
                timer: 1500,
                icon: "success",
                showConfirmButton: false,
              });
              TableListFunc();
            }
          },
          error: function () {
            if (LogoutProtect){
              window.location.href = "./?link=loclogout";
            } else {
              Swal.fire("Error", "Server tidak merespon, segera hubungi Administrator di ext. 3553 !","error" );
            }
          },
        });
      }
    });
  }

});

function LoadDataPIC() {
  $.ajax({
    type: "POST",
    url: `${restlocmkg}LoadDataPIC`,
    headers: {
      user: vusrnm,
      token: vtoken,
    },
    cache: false,
    success: function (response) {
      var obj = JSON.parse(response);
      if (obj.status == false) {
        Swal.fire(
          "Error",
          "Ada kendal di koneksi/database, data tidak tampil !",
          "error"
        );
      } else {
        $("#TbPICBody").empty();
        jmlData  = obj['data'].length;
        for (a = 0; a < jmlData; a++) {
          $("#EANListPIC").find("tbody").append(
            "<tr>"+
              "<td>"+obj['data'][a]['NamePic']+"</td>"+
              "<td><span class='DelPIC' IdPIC='"+obj['data'][a]['IdPic']+"' NmPIC='"+obj['data'][a]['NamePic']+"'><i class='trash red link icon'></i></td>"+
            "</tr>"
          );   
        }
      }
    },
    error: function () {
      if (LogoutProtect){
        window.location.href = "./?link=loclogout";
      } else {
        Swal.fire("Error", "Server tidak merespon, segera hubungi Administrator di ext. 3553 !","error" );
      }
    },
  });
}

