$(document).ready(function () {
  LoadDataEmailReceiver();
  LoadDataPIC();
  $("#addnewmkg").on("click", function () {
    window.location.href = "./?link=mkgreqnew";
  });

  $('#addnewpic').click(function(){
    $(".xwinaddnewpic").modal({ autofocus: false }).modal("show");
  });

  $('#addnewemailreceiver').click(function(){
    $(".xwinaddnewemailreceiver").modal({ autofocus: false }).modal("show");
  });

  $('#userguidemkg').on('click', function() {
    window.location.href = "./?link=guidemkg";
  });

  $('#tabTimeline').on('click', function() { 
    window.location.href = "./?link=mkgtimeline";
  });

  $('#tabMKG').on('click', function() { 
    window.location.href = "./?link=mkgindex";
  });

  $('#tabmkglist').on('click', function() { 
    window.location.href = "./?link=mkgindex";
  });

  $('#tabAppSetting').on('click', function() { 
    window.location.href = "./?link=mkgappsetting";
  });

  $('.CancelData').click(function(){
    $(".xwinaddnewpic").modal("hide");
    $(".xwinaddnewemailreceiver").modal("hide");
  });

  
  //Load Table
  TableListFunc();

  //RESPON KETIKA INPUT PENCARIAN DI KETIK
  $("#fcari_mkg").on("keyup", function () {
    var FilterCari = $("#fcari_mkg").val();
    var FilterNama = $("#fmn_mkg").val();
    var FilterTahun = $("#fthn_mkg").val();
    var FilterStatus = $("#fsts_mkg").val();
    var FilterProgress = $("#fprg_mkg").val();
    var FiterArry = [FilterCari, FilterNama, FilterTahun, FilterStatus, FilterProgress];
    var JsonFilter = JSON.stringify(FiterArry);
    setCookie("FilterMKGRequest", JsonFilter, 5);
    $("#MKGList").DataTable().destroy();
    TableListFunc();
    $("#MKGList").DataTable().page(0).draw();
  });


  //RESPON KETIKA FILTER TAHUN DI RUBAH
  $("#fmn_mkg, #fthn_mkg, #fsts_mkg, #fprg_mkg").on(
    "change",
    function () {
      var FilterCari = $("#fcari_mkg").val();
      var FilterNama = $("#fmn_mkg").val();
      var FilterTahun = $("#fthn_mkg").val();
      var FilterStatus = $("#fsts_mkg").val();
      var FilterProgress = $("#fprg_mkg").val();
      var FiterArry = [FilterCari, FilterNama, FilterTahun, FilterStatus, FilterProgress];
      var JsonFilter = JSON.stringify(FiterArry);
      setCookie("FilterMKGRequest", JsonFilter, 5);
      TableListFunc();
      $("#MKGList").DataTable().page(0).draw();
    }
  );

  //RESET FILTER
  $(".resetfilter").on("click", function () {
    var d = new Date();
    var yearnow = d.getFullYear();
    $("#fcari_mkg").val("");
    $("#fthn_mkg").dropdown("set selected", yearnow);
    $("#fmn_mkg").dropdown("set selected", "ALL");
    $("#fsts_mkg").dropdown("set selected", "ALL");
    $("#fprg_mkg").dropdown("set selected", "ALL");
    var FiterArry = ["", "ALL", yearnow, "ALL", "ALL"];
    var JsonFilter = JSON.stringify(FiterArry);
    setCookie("FilterMKGRequest", JsonFilter, 5);
    $("#MKGList").DataTable().destroy();
    TableListFunc("", "ALL", "ALL");
    $("#MKGList").DataTable().page(0).draw();
  });


});