function TableListFunc() {

  var NewDate = new Date();
  var YearNow = NewDate.getFullYear();

  var JsonFilter = getCookie('FilterPSCCreator');
  var ArrayF = JSON.parse(JsonFilter);
  if(JsonFilter){
    var SearchF   = ArrayF[0];
    var TahunF    = ArrayF[1];
    var NamaF     = ArrayF[2];
    var ProgressF = ArrayF[3];
    var StatusF   = ArrayF[4];
  } else {
    var SearchF   = "";
    var TahunF    = YearNow;
    var NamaF     = "ALL";
    var ProgressF = "ALL";
    var StatusF   = "ALL";
  }

  if (SearchF) {
    $("#fcari_mpp").val(SearchF);
    var SearchFStart = SearchF;
  } else {
    var SearchFStart = "";
  }
  if (TahunF) {
    $("#fthn_mpp").dropdown("set selected", TahunF);
    var TahunFStart = TahunF;
  } else {
    var TahunFStart = YearNow;
  }
  if (NamaF) {
    $("#fmn_mpp").dropdown("set selected", NamaF);
    var NamaFStart = NamaF;
  } else {
    var NamaFStart = "ALL";
  }
  if (ProgressF) {
    $("#fprg_mpp").dropdown("set selected", ProgressF);
    var ProgressFStart = ProgressF;
  } else {
    var ProgressFStart = "ALL";
  }
  if (StatusF) {
    $("#fsts_mpp").dropdown("set selected", StatusF);
    var StatusFStart = StatusF;
  } else {
    var StatusFStart = "ALL";
  }

  var FiterArry = [SearchFStart, TahunFStart, NamaFStart, ProgressFStart, StatusFStart];
  var JsonFilter = JSON.stringify(FiterArry);
  setCookie("FilterPSCCreator", JsonFilter, 5);

  $("#TableList").DataTable({
    dom:
      "<'ui stackable grid'" +
      "<'row'" +
      "<'left aligned sixteen wide column'>" +
      ">" +
      "<'row dt-table'" +
      "<'sixteen wide column'tr>" +
      ">" +
      "<'row'" +
      "<'left aligned three wide column'>" +
      "<'right aligned five wide column'i>" +
      "<'right aligned eight wide column'B>" +
      ">" +
      ">",
    buttons: [
      {
        text: "10",
        action: function (e, dt, node, config) {
          $("#TableList").DataTable().page.len(10).draw();
        },
      },
      {
        text: "25",
        action: function (e, dt, node, config) {
          $("#TableList").DataTable().page.len(25).draw();
        },
      },
      {
        text: "50",
        action: function (e, dt, node, config) {
          $("#TableList").DataTable().page.len(50).draw();
        },
      },
      {
        text: "100",
        action: function (e, dt, node, config) {
          $("#TableList").DataTable().page.len(100).draw();
        },
      },
      {
        text: "1000",
        action: function (e, dt, node, config) {
          $("#TableList").DataTable().page.len(1000).draw();
        },
      },
    ],
    destroy     : true,
    stateSave   : true,
    processing  : true,
    deferRender : true,
    serverSide  : true,
    orderClasses: false,
    scrollX     : true,
    scrollCollapse: true,
    scrollY     : '60vh',
    scroller: {
      loadingIndicator: true
    },
    fixedColumns:   {
      leftColumns: 3
    },
    pageLength  : 10,
    data        : [],
    language    : {
      sLengthMenu : "_MENU_",
      search      : "",
      searchPlaceholder: "Pencarian..",
      processing  : '<i class="spinner loading icon huge"></i>',
      info        : "_START_ sampai _END_ dari _TOTAL_ total data",
      paginate    : {
        "first"   : "Awal",
        "last"    : "Akhir",
        "next"    : "<i class='chevron right icon'></i>",
        "previous": "<i class='chevron left icon'></i>"
      },
      infoEmpty   : "Tidak ada data",
      select  : {
        rows  : ""
      }
    },
    order     : [[1, "desc"]],
    columns   : [
      { data: null,
        render: function (data) {
          const ArryPicMPP = data['designer_md'].split(", ");
          return `<div class="ui small teal compact labeled icon button itemmpp" id="${data['id']}" nompp="${data['no_mpp']}" picmpp="${ArryPicMPP[0]}" desc="${data['title']}" data-tooltip="Change PIC MPP" data-inverted="" data-position="right center" id="UploadItemMPP"><i class="exchange icon"></i>Change</div>`;
        },
      },
      { data  : 'no_mpp' },
      { data  : 'rev' },
      { data  : 'title' },
      { data  : 'jenis_project' },
      { data  : 'product' },
      { data: null,
        render: function (data) {
            return data['designer_md']+';<br>'+data['head_of_md'];
        },
      },
      { data: null,
        render: function (data) {
            return data['designer_id']+';<br>'+data['head_of_id'];
        },
      },
      { data  : 'project_leader' },
      { data  : 'negotiator' },
      { data  : 'status' },
      { data  : 'progress' },
    ],

    createdRow: function (row, data, index) {
      $(row).find("td:eq(2)").css("border-right", "inset");
      // $(row).find("td:eq(2)").css("border-right-color", ColorBorderFreeze);
    },

    ajax : {
      url     : `../RND_BackEnd/backend_mpp/mpp/list`,
      type    : "GET",
      headers : {
        user  : VUserName,
        token : VToken,
      },
      data    : {
        FSearch   : SearchFStart,
        FYear     : TahunFStart,
        FNama     : NamaFStart,
        FProgress : ProgressFStart,
        FStatus   : StatusFStart,
      },

      error: function () {
        IsTimeOut(LogoutProtect);
      },
    },
  });
}

$(document).ready(function () {

  TableListFunc();


  $("#addnewmpp").on("click", function () {
    window.location.href = "./?link=mppheader";
  });


  $("#TableList").on("click", ".itemmpp", function(){
    var uid   = $(this).attr('id');
    var nompp = $(this).attr('nompp');
    var pic   = $(this).attr('picmpp');
    var desc  = $(this).attr('desc');
    console.log(pic);
    $('#NoMPP').text(nompp);
    $('#Desc').text(desc);
    $('#PicLama').val(pic);
    $('#IdMPP').val(uid);
    $(".WindowChangePIC").modal({ autofocus: false, closable : false, }).modal("show");

    $('.fullsearch').dropdown({
      fullTextSearch:'exact', 
      match:'both',
      selectOnBlur:false,
      selectOnKeydown: false
    });

  });

  $('#SaveChangePIC').on('click', function() {
    $.ajax({
      type    : "POST",
      url     : '../RND_BackEnd/backend_mpp/mpp/change-pic',
      headers: {
        user  : VUserName,
        token : VToken
      },
      data: {
        username    : VUserName,
        pic_after   : $("#PicBaru").val(),
        reason      : $("#Reason").val(),
        id_mpp      : $("#IdMPP").val(),
      },
      cache: false,
      success: function (response){
        var obj = JSON.parse(response);
        if(isEmpty(obj)) {
          IsResponseEmptyObj();
        } else if(obj) {
          if (obj.status == false){
            IsResponseFalse(obj.message);
          } else {
            IsResponseTrue(obj.message);
            $(".WindowChangePIC").modal("hide");
            TableListFunc();
          }
        } else {
          IsResponseErrorElse();
        }
      },
      error: function () {
        IsTimeOut(LogoutProtect);
      },
    });
  });




  $(".CloseModal").on("click", function(){
    $(".WindowChangePIC").modal("hide");
  });


 
  //RESPON KETIKA INPUT PENCARIAN DI KETIK
  $("#fcari_mpp").on("keyup", function () {
    var FilterCari = $("#fcari_mpp").val();
    var FilterTahun = $("#fthn_mpp").val();
    var FilterNama = $("#fmn_mpp").val();
    var FilterProgress = $("#fprg_mpp").val();
    var FilterStatus = $("#fsts_mpp").val();
    var FiterArry = [FilterCari, FilterTahun, FilterNama, FilterProgress, FilterStatus];
    var JsonFilter = JSON.stringify(FiterArry);
    setCookie("FilterPSCCreator", JsonFilter, 5);
    $("#TableList").DataTable().destroy();
    TableListFunc();
    $("#TableList").DataTable().page(0).draw();
  });

  //RESPON KETIKA FILTER TAHUN DI RUBAH
  $("#fthn_mpp, #fmn_mpp, #fprg_mpp, #fsts_mpp").on(
    "change",
    function () {
      var FilterCari = $("#fcari_mpp").val();
      var FilterTahun = $("#fthn_mpp").val();
      var FilterNama = $("#fmn_mpp").val();
      var FilterProgress = $("#fprg_mpp").val();
      var FilterStatus = $("#fsts_mpp").val();
      var FiterArry = [FilterCari, FilterTahun, FilterNama, FilterProgress, FilterStatus];
      var JsonFilter = JSON.stringify(FiterArry);
      setCookie("FilterPSCCreator", JsonFilter, 5);
      TableListFunc();
      $("#TableList").DataTable().page(0).draw();
    }
  );

  //RESET FILTER
  $(".resetfilter").on("click", function () {
    var d = new Date();
    var YearNow = d.getFullYear();
    $("#fcari_mpp").val("");
    $("#fthn_mpp").dropdown("set selected", YearNow);
    $("#fmn_mpp").dropdown("set selected", "ALL");
    $("#fprg_mpp").dropdown("set selected", "ALL");
    $("#fsts_mpp").dropdown("set selected", "ALL");
    var FiterArry = ["", YearNow, "ALL", "ALL", "ALL"];
    var JsonFilter = JSON.stringify(FiterArry);
    setCookie("FilterPSCCreator", JsonFilter, 5);
    $("#TableList").DataTable().destroy();
    TableListFunc("", YearNow, "ALL", "ALL", "ALL");
    $("#TableList").DataTable().page(0).draw();
  });


});