function LoadDetailAppMoldLocal(a){
  $.ajax({
    type: "POST",
    url     : '../RND_BackEnd/backend_mpp/approval-report/approval-local/load-item/'+a,
    headers: {
      user  : VUserName,
      token : VToken
    },
    cache: false,
    success: function (data){
      var obj = JSON.parse(data);
      $(".WindowEditAppMoldLocal").modal({ autofocus: false, closable : false, }).modal("show");
      LoadDataHRIS();

      $("#MPICAppRD").dropdown("clear");
      $("#MPICAppMDE").dropdown("clear");
      if(obj.pic_app_rd){   
        $('#MPICAppRD')
        .find('option')
        .remove()
        .end()
        .append('<option selected value="'+obj.pic_app_rd+'">'+obj.pic_app_rd+'</option>')
        .val(obj.pic_app_rd);
      }

      if(obj.pic_app_mde){   
        $('#MPICAppMDE')
        .find('option')
        .remove()
        .end()
        .append('<option selected value="'+obj.pic_app_mde+'">'+obj.pic_app_mde+'</option>')
        .val(obj.pic_app_mde);
      }

      $('#MDrawingLoc').text(obj.drawing_loc);
      $('#MDescription').text(obj.description);
      $('#MMoldMaker').text(obj.mold_maker);
      $('#MDesigner').text(obj.designer);
      // $("#MPICAppRD").val(obj.pic_app_rd);
      // $("#MPICAppMDE").val(obj.pic_app_mde);
      $("#MLocalSupplier").text(obj.local_supplier);
      $("#MStartApp").val(obj.start_app);
      $('#MEndApp').val (obj.end_app);
      $(".DropdownResetVal").dropdown("clear");
      $("#MModifMutlak").dropdown("set selected",obj.modif_mutlak);
      $('#MCaused1').val(obj.caused1);
      $("#MModifIMP").dropdown("set selected",obj.modif_imp);
      $('#MCaused2').val(obj.caused2);
      $("#RepBefore").dropdown("set selected",obj.repair_before_trial);
      $('#MCaused3').val(obj.caused3);
      $("#MRepTrial").dropdown("set selected",obj.repair_saat_trial);
      $('#MCaused4').val(obj.caused4);
      $("#MTrial2Days").dropdown("set selected",obj.trial_2_days);
      $('#MCaused5').val(obj.caused5);
      $("#MReliability").dropdown("set selected",obj.reliabilty);
      $('#MCaused6').val(obj.caused6);
      $('#MId').val(obj.id_item_mpp);
    },
    error: function () {
      IsTimeOut(LogoutProtect);
    },
  });
}

function SaveEditItem() {
  $.ajax({
    type    : "POST",
    url     : '../RND_BackEnd/backend_mpp/approval-report/approval-local/edit-item',
    headers: {
      user  : VUserName,
      token : VToken
    },
    data: {
      id_item_mpp : $("#MId").val(),
      id_mpp      : IdMPP,
      username    : VUserName,
      pic_app_rd  : $("#MPICAppRD").val().toUpperCase(),
      pic_app_mde : $("#MPICAppMDE").val().toUpperCase(),

      drawing_loc   : $("#MDrawingLoc").text(),
      modif_mutlak  : $("#MModifMutlak").val(),
      caused1       : $("#MCaused1").val(),
      modif_imp     : $("#MModifIMP").val(),
      caused2       : $("#MCaused2").val(),
      repair_before_trial : $("#RepBefore").val(),
      caused3       : $("#MCaused3").val(),
      repair_saat_trial   : $("#MRepTrial").val(),
      caused4       : $("#MCaused4").val(),
      trial_2_days  : $("#MTrial2Days").val(),
      caused5       : $("#MCaused5").val(),
      reliabilty    : $("#MReliability").val(),
      caused6       : $("#MCaused6").val(),
      start_app     : $("#MStartApp").val(),
      end_app       : $("#MEndApp").val(),

    },
    cache: false,
    success: function (response){
      var obj = JSON.parse(response);
      if(isEmpty(obj)) {
        IsResponseEmptyObj();
      } else if(obj) {
        if (obj.status == false){
          IsResponseFalse(obj.message);
        } else {
          IsResponseTrue(obj.message);
          $(".WindowEditAppMoldLocal").modal("hide");
          TableListFunc();
        }
      } else {
        IsResponseErrorElse();
      }
    },
    error: function () {
      IsTimeOut(LogoutProtect);
    },
  });
}



function LoadDataHRIS() {

  $('.ui.search.dropdown.nama').dropdown({
    apiSettings: {
      url: '2_masterplasticpart/_autocompletenama_app.php?q={query}'
    },
    fields: {
      remoteValues : 'results',
      name         : 'name',
      value        : 'name'
    },
    filterRemoteData : false,
    saveRemoteData : true,
    clearable : true
  });

  $.ajax({
    type: "GET",
    url: "2_masterplasticpart/_artemis.php",
    cache: false,
    success: function (response) {
      setCookie('atoken', response, 1 / 24);
    },
    error: function () {
      IsTimeOut(LogoutProtect);
    },
  });
}


$(document).ready(function () {

  TableListFunc();
 
  $("#TableList").on("click", ".BtnEditAppMold", function(){
    var eid  = $(this).attr('eid');
    LoadDetailAppMoldLocal(eid);
  });
 
  $('#SaveData').on('click', function() {
    SaveEditItem();
  });

  $(".CloseModal").on("click", function(){
    $(".WindowEditAppMoldLocal").modal("hide");
  });

  $('[data-toggle="datepicker"]').datepicker({
    format: 'yyyy-mm-dd',
    autoHide: true,
    weekStart: 1
  });

  //RESPON KETIKA INPUT PENCARIAN DI KETIK
  $("#fcari_mpp").on("keyup", function () {
    var FilterCari = $("#fcari_mpp").val();
    var FilterTahun = $("#fthn_mpp").val();
    var FilterNama = $("#fmn_mpp").val();
    var FilterSales = $("#fsales_mpp").val();
    var FiterArry = [FilterCari, FilterTahun, FilterNama, FilterSales];
    var JsonFilter = JSON.stringify(FiterArry);
    setCookie("FilterMPPAppLocal", JsonFilter, 5);
    $("#TableList").DataTable().destroy();
    TableListFunc();
    $("#TableList").DataTable().page(0).draw();
  });

  //RESPON KETIKA FILTER TAHUN DI RUBAH
  $("#fthn_mpp, #fmn_mpp, #fsales_mpp").on(
    "change",
    function () {
      var FilterCari = $("#fcari_mpp").val();
      var FilterTahun = $("#fthn_mpp").val();
      var FilterNama = $("#fmn_mpp").val();
      var FilterSales = $("#fsales_mpp").val();
      var FiterArry = [FilterCari, FilterTahun, FilterNama, FilterSales];
      var JsonFilter = JSON.stringify(FiterArry);
      setCookie("FilterMPPAppLocal", JsonFilter, 5);
      TableListFunc();
      $("#TableList").DataTable().page(0).draw();
    }
  );

  //RESET FILTER
  $(".resetfilter").on("click", function () {
    var d = new Date();
    var YearNow = d.getFullYear();
    $("#fcari_mpp").val("");
    $("#fthn_mpp").dropdown("set selected", YearNow);
    $("#fmn_mpp").dropdown("set selected", "ALL");
    $("#fsales_mpp").dropdown("set selected", "ALL");
    var FiterArry = ["", YearNow, "ALL", "ALL"];
    var JsonFilter = JSON.stringify(FiterArry);
    setCookie("FilterMPPAppLocal", JsonFilter, 5);
    $("#TableList").DataTable().destroy();
    TableListFunc("", YearNow, "ALL", "ALL");
    $("#TableList").DataTable().page(0).draw();
  });


});