function RequestCancel(a,b,c){
  $("#BtnSaveRequestCancel").on("click", function () {
    var reason = $('#CancelReason').val().toUpperCase();
    Swal.fire({
      title: "<h3>Cancel IHP ini?</h3>",
      confirmButtonText: "Ya",
      showCancelButton: true,
    }).then((result) => {
      if (result.value) {
        $.ajax({
          type: "POST",
          url     : '../RND_BackEnd/backend_mpp/ihp/request-close-cancel',
          headers: {
            user  : VUserName,
            token : VToken
          },
          data: {   
            id          : a,
            type        : "cancel",
            username    : VUserName,
            reason      : reason,
            tahap       : b,
            localmaker  : c
          },
          cache: false,
          success: function (response) {
            var obj = JSON.parse(response);
            if(isEmpty(obj)) {
              IsResponseEmptyObj();
            } else if(obj) {
              if (obj.status == false){
                IsResponseFalse(obj.message);
              } else {
                IsResponseTrue(obj.message);
                $(".WindowRequestCancel").modal("hide");
                TableListFunc();
              }
            } else {
              IsResponseErrorElse();
            }
          },
          error: function () {
            IsTimeOut(LogoutProtect);
          },
        });
      }
    });
  });
}

//Print Label Acc
$("#BtnLabelAcc").on("click", function (e) {
  var idihp= $("#DetailIdIHP").val();
  var vend = $("#DetailVendor").text();
  var Thp = $('#DetailTahap').text();
  var Lkm = $('#DetailLocalMaker').val();
  (async () => {
    const { value: cetak } = await Swal.fire({
      html: "<h3>Jumlah cetak Label Acc</h3>",
      input: "select",
      inputOptions: {
        "1": "1",
        "2": "2",
        "3": "3",
        "4": "4",
      },
      showCancelButton: true,
    });
    if (cetak) {
      //menggunakan plugin tambahan redirect
      $.redirect(
        "./?link=cetaklabelaccmpp",
        { jumlah: cetak, idihp: idihp, vend: vend, thp: Thp, Lkm: Lkm },
        "POST",
        "_blank"
      );
    }
  })();
});


