$("#BtnCetakLabelMaterial").on("click", function (e) {
  var idihp= $("#DetailIdIHP").val();
  var vend = $("#DetailVendor").text();
  var Thp = $('#DetailTahap').text();
  var Lkm = $('#DetailLocalMaker').val();
  var Sec = $('#CetakMatBagian').val();
  var Len = Sec.length;
  if(Len > 0){
    $.redirect(
      "./?link=cetaklabelmaterialmpp",
      { Sec: Sec, idihp: idihp, vend: vend, thp: Thp, Lkm: Lkm },
      "POST",
      "_blank"
    );
  } else {
    IsResponseFalse("Silakan pilih bagian yang akan dicetak!");
  }

});


