function trimVendor(s) {
  while (s.substr(0,1) == '0' && s.length>1) { s = s.substr(1,9999); }
  return s;
}

function MDOprConfirm(){
  $("#BtnMDOprConfirm").on("click", function () {
    var id_ihp = $("#DetailIdIHP").val();
    var thp_ihp = $("#DetailTahap").text();
    var lkm_ihp = $("#DetailLkmIHP").val();
    Swal.fire({
      title: "<h3><i class='exclamation disabled large triangle icon'></i></h3>",
      text: 'Terima IHP dan Sample Material ini?',
      confirmButtonText: "Ya",
      showCancelButton: true,
    }).then((result) => {
      if (result.value) {
        $.ajax({
          type    : "POST",
          url     : `../RND_BackEnd/backend_mpp/ihp/confirm-mdoperator`,
          headers : {
            user  : VUserName,
            token : VToken
          },
          data: {   
            id_ihp   : id_ihp,
            username : VUserName,
            tahap    : thp_ihp,
            localmaker : lkm_ihp
          },
          cache: false,
          success: function (response) {
            var obj = JSON.parse(response);
            if(isEmpty(obj)) {
              IsResponseEmptyObj();
            } else if(obj) {
              if (obj.status == false){
                IsResponseFalse(obj.message);
              } else {
                IsResponseTrue(obj.message);
                TableListFunc();
                LoadDetailIHP(id_ihp,thp_ihp,lkm_ihp);
                $(".WindowAddEditIHP").modal("hide");
              }
            } else {
              IsResponseErrorElse();
            }
          },
          error: function () {
            IsTimeOut(LogoutProtect);
          },
        });
      }
    });
  });
}

function IHPSave(){
  $("#BtnSave").on("click", function () {
    var id_ihp = $("#DetailIdIHP").val();
    var thp_ihp = $("#DetailTahap").text();
    var tgl_sample = $("#TanggalInputSample").val();
    var tgl_spec   = $("#TanggalInputSpec").val();
    var lkm_ihp = $("#DetailLkmIHP").val();
    $.ajax({
      type    : "POST",
      url     : `../RND_BackEnd/backend_mpp/ihp/edit-item/md-opr`,
      headers : {
        user  : VUserName,
        token : VToken
      },
      data: {   
        id         : id_ihp,
        username   : VUserName,
        tanggal_input_sample : tgl_sample,
        tanggal_input_spec   : tgl_spec,
        tahap      : thp_ihp,
        localmaker : lkm_ihp
      },
      cache: false,
      success: function (response) {
        var obj = JSON.parse(response);
        if(isEmpty(obj)) {
          IsResponseEmptyObj();
        } else if(obj) {
          if (obj.status == false){
            IsResponseFalse(obj.message);
          } else {
            IsResponseTrue(obj.message);
            TableListFunc();
            LoadDetailIHP(id_ihp,thp_ihp,lkm_ihp);
            // $(".WindowAddEditIHP").modal("hide");
          }
        } else {
          IsResponseErrorElse();
        }
      },
      error: function () {
        IsTimeOut(LogoutProtect);
      },
    });
  });
}


