function SearchIHP(){
  $("#SearchNoIHP").on('keyup', function(){
    var src = $('#SearchNoIHP').val();
    var usr  = getCookie('usrnm');
    $.ajax({
      type: "POST",
      url: `${restlocpssihp}list_noihp`,
      headers: {
        user: vusrnm,
        token: vtoken,
      },
      data: { 
        src :src,
        usr: usr,
        pic: Udecrypt
      },
      cache: false,
      success: function (response){
        var obj = JSON.parse(response);
        if(isEmpty(obj)) {
          $('#Tb_SrcResult').hide();
          console.log('Gagal load tabel, data kosong');
        } else if(obj) {
          jmlData = obj.length;
          $('#Tb_SrcResult').show();
          $('#Tb_SrcResult').empty();
          for(a = 0; a < jmlData; a++){
            if(obj[a]["no_ihp"] === null){
              var noihp ='- - -';
            } else {
              var noihp = obj[a]["no_ihp"];
            }
            $('#Tb_SrcResult').append(
              "<tr class='hoveradd' id='"+obj[a]["no_ihp"]+"' idy='"+obj[a]["id_item"]+"'><td><i class='icon plus teal circle link'></i></td><td width='130px'>"+noihp+"</td><td width='660px'>"+obj[a]["description_item"]+"</td><td width='140px'>"+obj[a]["pic"]+"</td></tr>");
          }
          AddGroupIHP();
        }
      },
      error: function(){
        // window.location.href = "./?link=loclogout";
        console.log('Server tidak merespon, segera hubungi Administrator di ext. 3553 !');
      }
    });
  });
}

function AddGroupIHP(){
  $(".hoveradd").click(function(){
    var idx  = $(this).attr('id');
    var idy  = $(this).attr('idy');
    var usr  = getCookie('usrnm');
    var InputIHP = $("#NoIHPDistribute").val();
    var IdInputIHP = $("#IdNoIHPDistribute").val();
    if(InputIHP){
      var NewInputIHP = InputIHP+","+idx;
      var IdNewInputIHP = IdInputIHP+","+idy;
    } else {
      var NewInputIHP = idx;
      var IdNewInputIHP = idy;
    }

    var nameArr = NewInputIHP.split(',');
    var IdnameArr = IdNewInputIHP.split(',');
    var unique = nameArr.filter((value, index, array) => array.indexOf(value) === index);
    var Idunique = IdnameArr.filter((value, index, array) => array.indexOf(value) === index);
    var InputValueIHP = unique.toString();
    var IdInputValueIHP = Idunique.toString();

    $("#NoIHPDistribute").val(InputValueIHP);
    $("#IdNoIHPDistribute").val(IdInputValueIHP);
    console.log(unique);
            
  });
}

