function TableListFunc(FSearch, FYear, FIHP, FStatus) {
  $("#IHPList").DataTable({
    dom:
      "<'ui stackable grid'" +
      "<'row'" +
      "<'left aligned sixteen wide column'>" +
      ">" +
      "<'row dt-table'" +
      "<'sixteen wide column'tr>" +
      ">" +
      "<'row'" +
      "<'left aligned seven wide column'B>" +
      "<'left aligned two wide column'i>" +
      "<'right aligned seven wide column'p>" +
      ">" +
      ">",
    buttons: [
      {
        text: "10",
        action: function (e, dt, node, config) {
          $("#IHPList").DataTable().page.len(10).draw();
        },
      },
      {
        text: "25",
        action: function (e, dt, node, config) {
          $("#IHPList").DataTable().page.len(25).draw();
        },
      },
      {
        text: "50",
        action: function (e, dt, node, config) {
          $("#IHPList").DataTable().page.len(50).draw();
        },
      },
      {
        text: "100",
        action: function (e, dt, node, config) {
          $("#IHPList").DataTable().page.len(100).draw();
        },
      },
      {
        text: "1000",
        action: function (e, dt, node, config) {
          $("#IHPList").DataTable().page.len(1000).draw();
        },
      },
    ],
    destroy: true,
    stateSave: true,
    processing: true,
    deferRender: true,
    serverSide: true,
    orderClasses: false,
    pageLength: 10,
    scrollX     : true,
    data: [],
    language: {
      sLengthMenu: "_MENU_",
      search: "",
      searchPlaceholder: "Pencarian..",
      processing: '<i class="circle notch loading icon huge"></i>',
      info: "_START_ sampai _END_ dari _TOTAL_ total data",
      paginate: {
        "first":      "Awal",
        "last":       "Akhir",
        "next":       "Berikutnya",
        "previous":   "Sebelumnya"
      },
      infoEmpty:      "Tidak ada data",
      select: {
        rows: ""
      }
    },
    order: [[1, "desc"]],
    columns: [
      {
        data: null,
        render: function (data, type, row) {
          if (data['no_ihp'] != "") {
              return `<span class="viewdetail" idpss="${data['id_pss']}" iditem="${data['id_item']}" data-tooltip="View Detail IHP" data-inverted="" data-position="right center"><i class="tasks link circular icon "></i></span>`;
          } else {
            return "";
          }
        },
      },
      { data: 'tanggal_ihp' },
      { data: 'no_ihp' },
      { data: 'description_item' },
      {
        data: null,
        render: function (data, type, row) {
          if (data['file_upload'] != "") {
              return `<span class="downloadspec" data-tooltip="View Spec - ${data['file_upload']}" data-inverted="" data-position="right center" spec="${data['file_upload']}" iditem="${data['id_item']}"><i class="file circular red icon link"></i></span>`;
          } else {
            return ``;
          }
        },
      },
      { data: 'vendor' },
      { data: 'vendor_material_number' },
      { data: 'part_number' },
      {
        data: null,
        render: function (data, type, row) {
          if (data['product'] == "HOME APPLIANCES") {
            return `HA`;
          } else if (data['product'] == "AUDIO") {
            return `AUD`;
          } else if (data['product'] == "VIDEO") {
            return `VID`;
          } else if (data['product'] == "SPECIAL PRODUCT") {
            return `SPP`;
          } else {
            return `${data['product']}`;
          }
        },
      },
      { data: 'chasis' },
      { data: 'type' },
      { data: 'designer' },
      { data: 'operator' },
      { data: 'final_result' },
      { data: 'status_item' },
      { data: 'progress' },
    ],
    columnDefs: [
      { className: "center aligned", targets: [0,4,8,9]},
      { targets: [0], orderable: false },
      { targets: [1],
        render: function (data, type, row) {
          var dateObj = new Date(data);
          var momentObj = moment(dateObj);
          if (data){
            return momentObj.format('ddd, DD MMM YYYY');
          } else {
            return '';
          }
        }
      }
    ],

    ajax: {
      url: `${restlocpssihp}table_ihpviewer`,
      type: "POST",
      headers: {
        user: vusrnm,
        token: vtoken,
      },
      data: {
        FSearch: FSearch,
        FYear: FYear,
        FIHP: FIHP,
        FStatus: FStatus,
        User: Udecrypt,
        Section: Sdecrypt,
      },
      error: function () {
        window.location.href = "./?link=loclogout";
        Swal.fire(
          "Error",
          "Server tidak merespon, segera hubungi Administrator di ext. 3553 !",
          "error"
        );
      },
    },
  });
}


