function TableListFunc() {

  //Read Filter Cookies
  var d = new Date();
  var yearnow = d.getFullYear();

  var json_str = getCookie('FilterChangePICRelease');
  var arr = JSON.parse(json_str);
  if(json_str){
    var sfilter = arr[0];
    var mfilter = arr[1];
    var tfilter = arr[2];
    var stfilter = arr[3];
  } else {
    var sfilter = "";
    var mfilter = "ALL";
    var tfilter = yearnow;
    var stfilter = "ALL";
  }

  if (sfilter) {
    $("#fcari_mkg").val(sfilter);
    var sfilterstart = sfilter;
  } else {
    var sfilterstart = "";
  }
  if (mfilter) {
    $("#fmn_mkg").dropdown("set selected", mfilter);
    var mfilterstart = mfilter;
  } else {
    var mfilterstart = "ALL";
  }
  if (tfilter) {
    $("#fthn_mkg").dropdown("set selected", tfilter);
    var tfilterstart = tfilter;
  } else {
    var tfilterstart = yearnow;
  }
  if (stfilter) {
    $("#fst_mkg").dropdown("set selected", stfilter);
    var stfilterstart = stfilter;
  } else {
    var stfilterstart = "ALL";
  }

  var FiterArry = [sfilterstart, mfilterstart, tfilterstart, stfilterstart];
  var JsonFilter = JSON.stringify(FiterArry);
  setCookie("FilterChangePICRelease", JsonFilter, 5);

  var DTables = $("#MKGList").DataTable({
    dom:
      "<'ui stackable grid'" +
      "<'row'" +
      "<'left aligned sixteen wide column'>" +
      ">" +
      "<'row dt-table'" +
      "<'sixteen wide column'tr>" +
      ">" +
      "<'row'" +
      "<'left aligned seven wide column'B>" +
      "<'left aligned two wide column'i>" +
      "<'right aligned seven wide column'p>" +
      ">" +
      ">",
    buttons: [
      {
        text: "10",
        action: function (e, dt, node, config) {
          $("#MKGList").DataTable().page.len(10).draw();
        },
      },
      {
        text: "25",
        action: function (e, dt, node, config) {
          $("#MKGList").DataTable().page.len(25).draw();
        },
      },
      {
        text: "50",
        action: function (e, dt, node, config) {
          $("#MKGList").DataTable().page.len(50).draw();
        },
      },
      {
        text: "100",
        action: function (e, dt, node, config) {
          $("#MKGList").DataTable().page.len(100).draw();
        },
      },
      {
        text: "1000",
        action: function (e, dt, node, config) {
          $("#MKGList").DataTable().page.len(1000).draw();
        },
      },
    ],
    destroy: true,
    stateSave: true,
    processing: true,
    deferRender: true,
    serverSide: true,
    orderClasses: false,
    pageLength: 10,
    data: [],
    language: {
      sLengthMenu: "_MENU_",
      search: "",
      searchPlaceholder: "Pencarian..",
      processing: '<i class="circle notch loading icon huge"></i>',
      info: "_START_ sampai _END_ dari _TOTAL_ total data",
      paginate: {
        "first":      "Awal",
        "last":       "Akhir",
        "next":       "Berikutnya",
        "previous":   "Sebelumnya"
      },
      infoEmpty:      "Tidak ada data",
      select: {
        rows: ""
      }
    },
    order: [[0, "desc"]],
    columns: [
      { data: 'date' },
      { data: 'type' },
      { data: 'chasis' },
      { data: 'rom' },
      { data: 'pic_lama' },
      { data: 'pic_baru' },
    ],
   createdRow: function (row, data, index) {

      //WARNA PROCCESS
      if (data["pic"] == Udecrypt) {
        $(row).find("td").css("color", ColorOwner);
      }

      
    },

    ajax: {
      url: `${restlocswrelease}table_changepic`,
      type: "POST",
      headers: {
        user: vusrnm,
        token: vtoken,
      },
      data: {
        FSearch : sfilterstart,
        FNama   : mfilterstart,
        FTahun   : tfilterstart,
        FStatus : stfilterstart
      },

      error: function () {
        if (LogoutProtect){
          window.location.href = "./?link=loclogout";
        } else {
          Swal.fire("Error", "Server tidak merespon, segera hubungi Administrator di ext. 3553 !","error" );
        }
      },
    },
  });
}



