function TableListFunc(FSearch, FYear, FNama, FBagian) {
  $("#TCAList").DataTable({
    dom:
      "<'ui stackable grid'" +
      "<'row'" +
      "<'left aligned sixteen wide column'>" +
      ">" +
      "<'row dt-table'" +
      "<'sixteen wide column'tr>" +
      ">" +
      "<'row'" +
      "<'left aligned eight wide column'B>" +
      "<'right aligned eight wide column'p>" +
      ">" +
      ">",
    buttons: [
      {
        text: "10",
        action: function (e, dt, node, config) {
          $("#TCAList").DataTable().page.len(10).draw();
        },
      },
      {
        text: "25",
        action: function (e, dt, node, config) {
          $("#TCAList").DataTable().page.len(25).draw();
        },
      },
      {
        text: "50",
        action: function (e, dt, node, config) {
          $("#TCAList").DataTable().page.len(50).draw();
        },
      },
      {
        text: "100",
        action: function (e, dt, node, config) {
          $("#TCAList").DataTable().page.len(100).draw();
        },
      },
      {
        text: "1000",
        action: function (e, dt, node, config) {
          $("#TCAList").DataTable().page.len(1000).draw();
        },
      },
    ],
    destroy: true,
    stateSave: true,
    processing: true,
    deferRender: true,
    serverSide: true,
    orderClasses: false,
    pageLength: 10,
    data: [],
    language: {
      sLengthMenu: "_MENU_",
      search: "",
      searchPlaceholder: "Search..",
      processing: '<i class="circle notch loading icon huge"></i>',
    },
    order: [[0, "desc"]],
    columns: [
      { data: null,
        render: function (data, type, row) {
            return parseInt(data[0]).toString(16).toUpperCase();
        },
      },
      { data: null,
        render: function (data, type, row) {
            return data[2];
        },
      },
      { data: null,
        render: function (data, type, row) {
          if (data[12] !=="" ) {
            return `<i class="file outline red viewdoc icon" id="${data[0]}" num="${data[4]}" rev="${data[5]}" ext="${data[12]}">`;
          } else {
            return `<i class="file outline greyish icon">`;
          }
        },
      },
      { data: 4 },
      { data: 5 },
      { data: 8 },
      { data: 6 },
      { data: 10 },
      { data: null,
        render: function (data, type, row) {
          if ( data[10] == Udecrypt ) {
            return `<i class="edit outline edittca iconcoloredit icon" id="${data[0]}" num="${data[4]}" chs="${data[8]}" des="${data[6]}" rev="${data[5]}">`;
          } else {
            return "";
          }
        },
      },
    ],
    columnDefs: [
      { className: "center aligned", targets: [0,1,2,4,8] },
      { className: "collapsing", targets: [0,1,3] },
      { orderable: false, targets: [8] },
    ],

    ajax: {
      url: `${restlocvideonum}table_ta`,
      type: "POST",
      headers: {
        user: vusrnm,
        token: vtoken,
      },
      data: {
        FSearch: FSearch,
        FYear: FYear,
        FNama: FNama,
      },
      error: function () {
        // window.location.href = "./?link=loclogout";
        Swal.fire(
          "Error",
          "Server tidak merespon, segera hubungi Administrator di ext. 3553 !",
          "error"
        );
      },
    },
  });

}


$(document).ready(function () {

  $("#addnewtca").click(function(){
    $(".additem").modal('show');
    $('#inputchs').val('')
    $('#inputtype').val('')
    $('#inputnote').val('')
  });

  $("#uploadtca").click(function(){
    var id   = $('#einputid').val();
    var num  = $('#einputnum').val();
    $("#uinputid").val(id);
    $("#uinputnum").val(num);
    $("#utcanumber").text(num);
    $(".uploaditem").modal('show');
    $(".edititem").modal('hide');
  });

  $("#TCAList").on("click", ".edittca", function () {
    var id  = $(this).attr("id");
    var num = $(this).attr("num");
    var chs = $(this).attr("chs");
    var des = $(this).attr("des");
    var rev = $(this).attr("rev");
    var ary = num.split('.');
    $("#tcanumber").text(num);
    $("#einputid").val(id);
    $("#einputchs").val(chs);
    $("#einputdesc").val(des);
    $("#einputnum").val(num);
    $("#einputjenis").dropdown("clear");
    $("#einputjenis").dropdown("set selected", ary[1]);
    $(".edititem").modal('show');

    if(rev){
      $(".diveditrev").hide();
      setTimeout(function() { $('#einputdesc').focus() }, 1000);
    } else {
      $(".diveditrev").show();
    }
  });


  $("#TCAList").on("click", ".viewdoc", function () {
    var num = $(this).attr("num").replaceAll("/", ".");
    var rev = $(this).attr("rev");
    var ext = $(this).attr("ext");
    window.open("../RND_Upload/VideoDoc/TA_"+num+rev+"."+ext, "_blank");
  });

  $("#tabtmpb").click(function(){
    window.location.href = "./?link=vidindex";
  });
  $("#tabta").click(function(){
    window.location.href = "./?link=vidindex&sub=TA";
  });
  $("#tabsercom").click(function(){
    window.location.href = "./?link=vidindex&sub=SC";
  });
  $("#tabcpv").click(function(){
    window.location.href = "./?link=vidindex&sub=CPV";
  });
  $("#tabdrr").click(function(){
    window.location.href = "./?link=vidindex&sub=DRR";
  });
  $("#tabfamily").click(function(){
    window.location.href = "./?link=vidindex&sub=FAM";
  });
  $("#tabsetting").click(function(){
    window.location.href = "./?link=vidindex&sub=SET";
  });


});

//SAVE NEW TA
$('#savedata').on('click', function(e){
  var ichss = $('#inputchs').val();
  var ijnis = $('#inputjenis').val();
  var idesc = $('#inputdesc').val();
  $.ajax({
    type: "POST",
    url: `${restlocvideonum}create_ta`,
    headers : {
      user  : vusrnm,
      token : vtoken
    },
    data: {
      ichss     : ichss,
      ijnis     : ijnis,
      idesc     : idesc,
    },
    cache: false,
    success: function (response){
      var obj = JSON.parse(response);
      if(isEmpty(obj)) {
        console.log('Gagal Simpan mas brooo, data kosong');
      } else if(obj) {
        if (obj.status == false){
          Swal.fire({
            text: obj.message,
            timer: 1500,
            icon: 'warning',
            showConfirmButton: false
          })
        } else {
          Swal.fire({
            title: 'Berhasil',
            text: obj.message,
            timer: 1500,
            position: 'top-end',
            icon: 'success',
            showConfirmButton: false
          })
        }
        $(".additem").modal('hide');
        var d = new Date();
        var yearnow = d.getFullYear();
        var json_str = getCookie('FilterTCA');
        var arr = JSON.parse(json_str);
        if(json_str){
          var sfilter = arr[0];
          var tfilter = arr[1];
          var mfilter = arr[2];
        } else {
          var sfilter = "";
          var tfilter = yearnow;
          var mfilter = "ALL";
        }
        TableListFunc( sfilter, tfilter, mfilter);

      } else {
        Swal.fire('Error','Ada kendal di koneksi/database, data tidak tersimpan !','error')
      }
    },
    error: function () {
      if (LogoutProtect){
        window.location.href = "./?link=loclogout";
      } else {
        Swal.fire("Error", "Server tidak merespon, segera hubungi Administrator di ext. 3553 !","error" );
      }
    },
  });
});


