//REVISI DATA
$('#revisidata').on('click', function(e){

  var iid   = $('#einputid').val();
  var inum  = $('#einputnum').val();

  Swal.fire({
      title: '<h3>Revisi No '+inum+'?</h3>',
      icon: 'info',
      confirmButtonText: 'Ya',
      showCancelButton: true
    }).then((result) => {
      if (result.value) {
        $(".edititem").modal('hide');
        $.ajax({
          type: "POST",
          url: `${restlocvideonum}revisi_tca`,
          headers : {
            user  : vusrnm,
            token : vtoken
          },
          data: {
            inum  : inum,
            iid   : iid
          },
          cache: false,
          success: function (response){
            var obj = JSON.parse(response);
            if(isEmpty(obj)) {
              Swal.fire('Error','Server error, Data mungkin bermasalah !','error');
            } else if (obj.code == "error"){
              Swal.fire('',obj.message,'error');
            } else {
              Swal.fire('Berhasil','Nomor berhasil direvisi.','success');
              var d = new Date();
              var yearnow = d.getFullYear();
              var json_str = getCookie('FilterTCA');
              var arr = JSON.parse(json_str);
              if(json_str){
                var sfilter = arr[0];
                var tfilter = arr[1];
                var mfilter = arr[2];
              } else {
                var sfilter = "";
                var tfilter = yearnow;
                var mfilter = "ALL";
              }
              TableListFunc( sfilter, tfilter, mfilter);
            }
          },
          error: function(){
            Swal.fire('Error','Server tidak merespon, segera hubungi Webmaster !','error')
          }
        });
      }
    })

});


// Upload TA Dokumen
$("#saveupload").on("click", function () {
  var file_data = $("#uinputfile").prop("files")[0];
  var idtca    = $('#uinputid').val();
  var numtca   = $('#uinputnum').val();
  var form_data = new FormData();
  form_data.append("file", file_data);
  form_data.append("idtca", idtca);
  form_data.append("numtca", numtca);
  $.ajax({
    type: "POST",
    dataType: "text",
    contentType: false,
    processData: false,
    url: `${restlocvideonum}upload_tca`,
    headers: {
      user: vusrnm,
      token: vtoken,
    },
    data: form_data,
    cache: false,
    success: function (response) {
      var obj = JSON.parse(response);
      if (obj.status == false) {
        Swal.fire({
          text: obj.message,
          timer: 1500,
          icon: "warning",
          showConfirmButton: false,
        });
      } else {
        Swal.fire({
          text: "Uploaded !",
          timer: 2000,
          toast: true,
          showConfirmButton: false,
        });
        var d = new Date();
        var yearnow = d.getFullYear();
        var json_str = getCookie('FilterTCA');
        var arr = JSON.parse(json_str);
        if(json_str){
          var sfilter = arr[0];
          var tfilter = arr[1];
          var mfilter = arr[2];
        } else {
          var sfilter = "";
          var tfilter = yearnow;
          var mfilter = "ALL";
        }
        TableListFunc( sfilter, tfilter, mfilter);
        $(".uploaditem").modal("hide");
      }
    },
    error: function () {
      if (LogoutProtect){
        window.location.href = "./?link=loclogout";
      } else {
        Swal.fire("Error", "Server tidak merespon, segera hubungi Administrator di ext. 3553 !","error" );
      }
    },
  });
});


