//Read Filter Cookies
var d = new Date();
var yearnow = d.getFullYear();

var json_str = getCookie('FilterTCA');
var arr = JSON.parse(json_str);
if(json_str){
  var sfilter = arr[0];
  var tfilter = arr[1];
  var mfilter = arr[2];
} else {
  var sfilter = "";
  var tfilter = yearnow;
  var mfilter = "ALL";
}

if (sfilter) {
  $("#fcari_tca").val(sfilter);
  var sfilterstart = sfilter;
} else {
  var sfilterstart = "";
}
if (tfilter) {
  $("#fthn_tca").dropdown("set selected", tfilter);
  var tfilterstart = tfilter;
} else {
  var tfilterstart = yearnow;
}
if (mfilter) {
  $("#fmn_tca").dropdown("set selected", mfilter);
  var mfilterstart = mfilter;
} else {
  var mfilterstart = "ALL";
}

var FiterArry = [sfilterstart, tfilterstart, mfilterstart];
var JsonFilter = JSON.stringify(FiterArry);
setCookie("FilterTCA", JsonFilter, 5);

//Load Table
TableListFunc( sfilterstart, tfilterstart, mfilterstart);

//RESPON KETIKA INPUT PENCARIAN DI KETIK
$("#fcari_tca").on("keyup", function () {
  var FilterCari = $("#fcari_tca").val();
  var FilterTahun = $("#fthn_tca").val();
  var FilterNama = $("#fmn_tca").val();
  
  var FiterArry = [FilterCari, FilterTahun, FilterNama];
  var JsonFilter = JSON.stringify(FiterArry);
  setCookie("FilterTCA", JsonFilter, 5);
  $("#PSSList").DataTable().destroy();
  TableListFunc(FilterCari, FilterTahun, FilterNama);
});

//RESPON KETIKA FILTER TAHUN DI RUBAH
$("#fthn_tca, #fmn_tca").on(
  "change",
  function () {
    var FilterCari = $("#fcari_tca").val();
    var FilterTahun = $("#fthn_tca").val();
    var FilterNama = $("#fmn_tca").val();
   
    var FiterArry = [FilterCari, FilterTahun, FilterNama];
    var JsonFilter = JSON.stringify(FiterArry);
    setCookie("FilterTCA", JsonFilter, 5);
    TableListFunc(FilterCari, FilterTahun, FilterNama);
  }
);

//RESET FILTER
$(".resetfilter").on("click", function () {
  var d = new Date();
  var yearnow = d.getFullYear();
  $("#fcari_tca").val("");
  $("#fthn_tca").dropdown("set selected", yearnow);
  $("#fmn_tca").dropdown("set selected", "ALL");
  var FiterArry = ["", yearnow, "ALL"];
  var JsonFilter = JSON.stringify(FiterArry);
  setCookie("FilterTCA", JsonFilter, 5);
  $("#PSSList").DataTable().destroy();
  TableListFunc("", yearnow, "ALL");
});
